mapper.select:
SELECT u.id, u.username, u.password, u.enabled
FROM users u
WHERE u.username = #{username}

mapper.update:
UPDATE users
SET password = #{password},
    enabled  = #{enabled}
WHERE username = #{username}

mapper.select:
SELECT COUNT(*)
FROM users
WHERE username = #{username}

SELECT id
FROM users
WHERE username = #{username}

SELECT COUNT(*) > 0
FROM user_roles
WHERE user_id = #{userId}
  AND role_id = #{roleId}

mapper.update:
UPDATE users
SET password = #{newPassword}
WHERE username = #{username}
