mapper.select:
SELECT user_id, user_type, status, nick_name, avatar, created_time, updated_time
FROM user_profile
WHERE user_id = #{userId}

mapper.update:
UPDATE user_profile
SET status = #{status}
WHERE user_id = #{userId}

