mapper.select:
SELECT user_id, user_type, nick_name, avatar, created_time, updated_time
FROM user_profile
WHERE user_id = #{userId}

mapper.update:
UPDATE user_profile
SET user_type = #{userType},
    nick_name = #{nickName},
    avatar    = #{avatar}
WHERE user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM user_profile
WHERE user_id = #{userId}

mapper.update:
UPDATE user_profile
SET nick_name = #{nickName}
WHERE user_id = #{userId}

UPDATE user_profile
SET avatar = #{avatar}
WHERE user_id = #{userId}
