mapper.select:
SELECT up.id, up.account, up.user_type, up.nick_name, up.user_portrait, up.created_time, up.updated_time
FROM user_profile up
WHERE up.account = #{account}

mapper.update:
UPDATE user_profile
SET user_type = #{userType},
    nick_name = #{nickName},
    user_portrait = #{userPortrait}
WHERE account = #{account}

mapper.select:
SELECT COUNT(*)
FROM user_profile
WHERE account = #{account}

mapper.update:
UPDATE user_profile
SET nick_name = #{nickName}
WHERE account = #{account}

UPDATE user_profile
SET user_portrait = #{userPortrait}
WHERE account = #{account}
